/*global kakao*/

import React, { useEffect, useState } from "react";

function KakaoMap() {
    const [coords, setCoords] = useState({
        latitude: 33.450701,
        longitude: 126.570667,
    });
    const [location, setLocation] = useState("");

    useEffect(() => {
        navigator.geolocation.getCurrentPosition((data) => {
            setCoords({
                ...coords,
                latitude: data.coords.latitude,
                longitude: data.coords.longitude,
            });
        });
    }, []);

    useEffect(() => {
        console.log(location);

        const infowindow = new kakao.maps.InfoWindow({ zIndex: 1 });
        const container = document.getElementById("map");
        const options = {
            center: new kakao.maps.LatLng(coords.latitude, coords.longitude),
            level: 3,
        };
        const map = new kakao.maps.Map(container, options);
        const ps = new kakao.maps.services.Places();
        const geocoder = new kakao.maps.services.Geocoder();

        searchAddrFromCoords(map.getCenter(), displayCenterInfo);

        kakao.maps.event.addListener(map, "dragend", function () {
            searchAddrFromCoords(map.getCenter(), displayCenterInfo);
        });

        function searchAddrFromCoords(coords, callback) {
            // 좌표로 행정동 주소 정보를 요청합니다
            geocoder.coord2RegionCode(
                coords.getLng(),
                coords.getLat(),
                callback
            );

            const center = map.getCenter();
            console.log(coords.getLng(), coords.getLat());
            console.log(map.getCenter());
            setCoords({
                ...coords,
                latitude: center.La,
                longitude: center.Ma,
            });
        }

        function displayCenterInfo(result, status) {
            if (status === kakao.maps.services.Status.OK) {
                const infoDiv = document.getElementById("centerAddr");
                for (var i = 0; i < result.length; i++) {
                    // 행정동의 region_type 값은 'H' 이므로
                    if (result[i].region_type === "H") {
                        infoDiv.innerHTML = result[i].address_name;
                        const address = result[i].address_name.split(" ");
                        setLocation(address[2]);
                        break;
                    }
                }
            }
        }

        ps.keywordSearch(`${location} 코인 노래방`, placesSearchCB);

        function placesSearchCB(data, status, pagination) {
            if (status === kakao.maps.services.Status.OK) {
                var bounds = new kakao.maps.LatLngBounds();

                for (var i = 0; i < data.length; i++) {
                    displayMarker(data[i]);
                    bounds.extend(new kakao.maps.LatLng(data[i].y, data[i].x));
                }
            }
        }

        function displayMarker(place) {
            var marker = new kakao.maps.Marker({
                map: map,
                position: new kakao.maps.LatLng(place.y, place.x),
            });

            kakao.maps.event.addListener(marker, "click", function () {
                infowindow.setContent(
                    '<div style="padding:5px;font-size:12px;">' +
                        place.place_name +
                        "</div>"
                );
                infowindow.open(map, marker);
            });
        }
    }, [coords, location]);

    return (
        <>
            <div id="map" style={{ width: "100vw", height: "100vh" }} />
            <div
                className="hAddr"
                style={{
                    backgroundColor: "red",
                    position: "absolute",
                    top: 0,
                    left: 0,
                    zIndex: 10,
                }}
            >
                <span className="title">지도중심기준 행정동 주소정보</span>
                <span id="centerAddr"></span>
            </div>
        </>
    );
}

export default KakaoMap;
